INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26350, 'Roviaprega 150mg 20 cap.', 'روفيابريجا 150مجم 20 كبسول', '38', '', 'Anti-epileptic.gaba analogs', 'Pregabalin', 'Marcyrl co. > bahrain pharma', 'Cap', 'كبسولة', '150mg', '2', '', 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalinspregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', '', '', 783, '2022-07-14', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Pregabalin is structurally similar to gamma-aminobutyric acid (GABA) - an inhibitory neurotransmitter.[A173995] It may be used to manage neuropathic pain, postherpetic neuralgia, and fibromyalgia among other conditions.[A187190] Although as per the FDA Label the mechanism of action has not been definitively defined, there is evidence that pregabalin exerts its effects by binding to the α2δ subunit of voltage-dependent calcium channels.[A187190][L7066] Pregabalin is marketed by Pfizer under the trade name Lyrica and Lyrica Cr (extended release).[L1006][L7066] It may have dependence liability if misused but the risk appears to be highest in patients with current or past substance use disorders.[A31161]', '["N03AX16"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26351, 'Rovitan 14 sachets', 'روفيتان 14 كيس', '98', '', 'Iron supplement', 'Lactoferrin+vitamin c+zinc', 'Globe international pharmaceuticals', 'Sachet', 'أكياس', '', '1', 'مصدر للحديد -- علاج حالات الانيميا --مصدر للزنك و فيتامين سي -- مقوي للمناعة', 'lactoferrin is a bioactive 80-kda iron binding glycoprotein. indication & dosage: **for prophylaxis: 1-6 years: 1 sachet / day for 3-4 weeks. over 6 years: 2 sachet / day for 3-4 weeks. **during infections (upper respiratory diarrhea): 1-6 years: 1 sachet', '6224008365471', '', 3984, '2023-02-04', 0, NULL, NULL, NULL, NULL, 0, 0, 0, NULL, NULL, NULL);
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26353, 'Rowadinafil 20 mg 4 f.c. tab', 'روادينافيل 20مجم 4 اقراص', '29', '', 'Tonic for men', 'Vardenafil', 'Al rowad pharmaceutical industrial co.', 'Tab', 'أقراص', '20 mg', '1', '', '', '', '', 1412, '2022-07-14', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Vardenafil (Levitra) is an oral therapy for the treatment of erectile dysfunction. It is a selective inhibitor of cyclic guanosine monophosphate (cGMP)-specific phosphodiesterase type 5 (PDE5). Penile erection is a hemodynamic process initiated by the relaxation of smooth muscle in the corpus cavernosum and its associated arterioles. During sexual stimulation, nitric oxide is released from nerve endings and endothelial cells in the corpus cavernosum. Nitric oxide activates the enzyme guanylate cyclase resulting in increased synthesis of cyclic guanosine monophosphate (cGMP) in the smooth muscle cells of the corpus cavernosum. The cGMP in turn triggers smooth muscle relaxation, allowing increased blood flow into the penis, resulting in erection. The tissue concentration of cGMP is regulated by both the rates of synthesis and degradation via phosphodiesterases (PDEs). The most abundant PDE in the human corpus cavernosum is the cGMPspecific phosphodiesterase type 5 (PDE5); therefore, the inhibition of PDE5 enhances erectile function by increasing the amount of cGMP.', '["G04BE09"]', '[]');
