INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26382, 'Royalsteride 5 mg 20 f.c. tabs.', 'رويالستيرايد 5مجم 20 قرص', '52', '', '5 alpha-reductase inhibitor', 'Finasteride', 'Hi-pharm > royal link pharma', 'Tab', 'أقراص', '5 mg', '2', 'علاج حالات تضخم البروستاتا الحميد -- علاج في بعض حالات الصلع وتساقط الشعر', 'about finasteride 5 alpha-reductase inhibitor synthetic anti-androgen. mechanism of action of finasteride this synthetic 4-azasteroid derivative competitively inhibits steroid 5alpha reductase which is an enzyme responsible for active androgen dehydrotest', '', '', 10212, '2023-01-11', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Finasteride is a synthetic 4-azasteroid compound [L10565] and specific inhibitor of steroid Type II 5α-reductase, which is an intracellular enzyme that converts the androgen testosterone into 5α-dihydrotestosterone (DHT). It works in a similar fashion as [dutasteride], which is another 5-alpha-reductase inhibitor, by exerting antiandrogenic effects. Finasteride is an orally active drug that was first approved by the FDA in 1992 for the treatment of benign prostatic hyperplasia to improve symptoms and reduce the risk for acute urinary retention or the need for surgical procedures.[L6244,L10565] In 1998, it was approved by the FDA to treat male pattern hair loss.[L6244] Finasteride is commonly marketed under the brand names Propecia and Proscar to be used aloneo or in combination with [doxazosin], an alpha-blocker.

Both benign prostatic hyperplasia and androgenic alopecia are androgen-dependent disorders that are characterized by _in situ_ high levels of DHT.[A178159] In the treatment of benign prostate hyperplasia, alpha-blockers such as [tamsulosin] and [terazosin] are also used. Compared to alpha-blockers that focus on providing the rapid relief of symptoms, 5α-reductase inhibitors aim to target the underlying disease by blocking the effects of the primary androgen involved in benign prostate hyperplasia and androgenic alopecia, thus reducing the risk for secondary complications while providing symptom control.[A2132]', '["D11AX10", "G04CA51", "G04CB01"]', '[]');
