INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26886, 'Sevoflurane 100% liquid for inhalation 250 ml', 'سيفوفلوران 100% سائل الاستنشاق 250 مل', '3,715', '1250', 'General anaesthesia', 'Sevoflurane', 'Arab caps > acdima international trading', 'Unknown', 'غير محدد', '100%', '1', '', 'about sevoflurane volatile halogenated anaesthetics sweet-smelling non-flammable highly fluorinated methyl isopropyl ether mechanism of action of sevoflurane sevoflurane induces a reduction in junctional conductance by decreasing gap junction channel open', '', '', 2298, '2024-10-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Sevoflurane is an ether inhalation anesthetic agent used to induce and maintain general anesthesia.[L42340] It is a volatile, non-flammable compound with a low solubility profile and blood/gas partition coefficient.[L42340] Sevoflurane was patented in 1972, was approved for clinical use in Japan in 1990, and approved by the FDA in 1996.[A249885] Sevoflurane is three times more potent than [desflurane], but has lower potency compared to [halothane] and [isoflurane]. Unlike other volatile anesthetics, sevoflurane has a pleasant odor and does not irritate the airway. The hemodynamic and respiratory depressive effects of sevoflurane are well tolerated, and most patients receiving this anesthetic agent present little toxicity.[A249910] Therefore, it can be used for inhalational induction in adults and children for a wide variety of anesthetic procedures.[A249885]', '["N01AB08"]', '[]');
