INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26976, 'Siloprostate 4 mg 30 caps.', 'سيلوبروستات 4مجم 30 كبسول', '81', '', 'Urination-difficulty.alpha blocker', 'Silodosin', 'October pharma', 'Cap', 'كبسولة', '4 mg', '3', '', 'it s selective alpha 1 a blocker used to treat symptoms associated with (bph) benign prostate hyperplasia recommended dose : once daily.', '', '', 914, '2022-07-11', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Silodosin is a selective antagonist of alpha(α)-1 adrenergic receptors that binds to the α1Asubtype with the highest affinity. α1-adrenergic receptors regulate smooth muscle tone in the bladder neck, prostate, and prostatic urethra: the α1Asubtype accounts for approximately 75% of α1-adrenoceptors in the prostate.[A231159] 

Silodosin was first approved by the FDA in October 2008 [A231159] and it is also approved in Europe and Canada. Silodosin is available as oral capsules with common trade names Rapaflo and Urorec. It is indicated for the symptomatic treatment of benign prostatic hyperplasia in adults.[L32368] Most commonly affecting males over the age of 40 years, benign prostatic hyperplasia is the non-malignant enlargement of the prostate gland, associated with lower urinary tract symptoms that have a negative impact on the quality of life of patients.[A231159] Silodosin works by binding to α1A-adrenoceptors with high affinity and relaxing the lower urinary tract, thereby improving urinary symptoms and alleviating bladder outlet obstruction.[A231229]', '["G04CA04"]', '[]');
