INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27085, 'Sleepez 1 mg 20 f.c.tabs.', 'سليبيز 1مجم 20 قرص', '32', '21', 'Psychiatric.anxiolytics-hypnotics', 'Eszopiclone', 'Chemipharm', 'Tab', 'أقراص', '1 mg', '2', '', '', '6222006500603', '', 1505, '2025-08-04', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Eszopiclone, marketed by Sepracor under the brand-name Lunesta, is a nonbenzodiazepine hypnotic drug used to treat insomnia. It is the active stereoisomer of zopiclone, belonging to the class of drugs known as _cyclopyrrolones_.[A179638,L6850] Cyclopyrrolone drugs demonstrate high efficacy and low toxicity[A179827], offering a safer alternative to other drugs used for insomnia.

