INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27111, 'Snafi 20 mg 2 f.c.tab', 'سنافي 20 مجم 2 قرص', '148', '148', 'Tonic for men', 'Tadalafil', 'Spimaco > eimc', 'Tab', 'أقراص', '20 mg', '2', 'مقوي للرجال', '', '6281086001783', '', 3059, '2025-08-16', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Tadalafil is a selective phosphodiesterase-5 inhibitor that is used in the treatment of erectile dysfunction (ED), pulmonary arterial hypertension (PAH), and benign prostatic hypertrophy.[L39100, L39105] It was first approved in 2003 by the FDA for use in ED and later in 2009 for PAH. In contrast to other PDE5 inhibitors like [sildenafil], tadalafil has greater selectivity for PDE5 and a longer half-life which has made it a more suitable option for chronic once-daily dosing in the treatment of PAH.[A242287]', '["G04BE08", "C02KX52", "G04CA54"]', '[]');
