INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27986, 'Tegsoran 6mg 10 tab.', 'تيجسوران 6مجم 10 اقراص', '15', '', 'Motility stimulant', 'Tegaserod', 'Delta pharma', 'Tab', 'أقراص', '6mg', '1', '', 'about tegaserod serotonin 5-ht4 agonist a motility stimulant. mechanism of action of tegaserod tegaserod is a 5ht4 agonist with negligible affinity for other receptor subtypes. binding of tegaserod to the 5ht4 receptor will enhances gastrointestinal motil', '', '', 796, '2022-07-30', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Novartis'' brand name Zelnorm (tegaserod) had originally received approval from the US FDA in 2002 for the treatment of irritable bowel syndrome with constipation (IBS-C).[L5918,F4229] It was, however, voluntarily withdrawn from widespread use in the US market in 2007 after concerns arose over the possibility that tegaserod could potentially cause dangerous cardiovascular events in patients.[L5918,F4229] Since then, closer evaluations of the original data suggesting such cardiovascular risk have resulted in the limited reintroduction or ''re-approval'' of tegaserod for treatment of IBS-C specifically in female patients less than 65 years of age and whom are considered to be at a lower risk of a cardiovascular event than the broader population.[L5918,F4229] Zelnorm (tegaserod) by Sloan Pharma subsequently gained re-approval in April of 2019.[L5918] Nevertheless, tegaserod remains un-approved in certain regions.[F4226]

Despite the relative complications involved in its history of regulatory approval, ever since its first introduction in 2002 tegaserod remains the only therapy for IBS-C that possesses the unique mechanism of action of acting on serotonin-4 (5-HT(4)) receptors in smooth muscle cells and in the gastrointestinal wall to facilitate actions like esophageal relaxation, peristaltic gut movement, and natural secretions in the gut, among others.[L5918,A11044,A176762,F4229,F4223]', '["A06AX06"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27987, 'Tekliva 0.004% eye drops 2.5 ml', 'تيكليفا 0.004% قطرة للعين 2.5مل', '79', '48', 'Antiglaucoma', 'Travoprost', 'Eva pharma', 'Drops', 'نقط', '0.004%', '1', '', 'about travoprost a synthetic prostaglandin f2a analogue ophthalmic antiglaucoma. mechanism of action of travoprost travoprost is a prostaglandin f2-alpha analogue and it is a prostanoid selective fp receptor agonist which reduces the intraocular pressure', '', '', 1037, '2025-09-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Travoprost ophthalmic solution is a topical medication used for controlling the progression of glaucoma or ocular hypertension, by reducing intraocular pressure [FDA Label]. It is a synthetic prostaglandin F2alpha analog [FDA Label]. Having been a well-received therapeutic agent with demonstrated efficacy and safety, travoprost is currently approved by the US FDA as a first-line treatment for lowering intraocular pressure in patients with open-angle glaucoma or ocular hypotension [L5155]. Furthermore, this approval also solidifies the medication as the first and only prostaglandin analog approved by the FDA for first-line treatment of glaucoma patients that does not contain the preservative benzalkonium chloride [L5155]. Moreover, travoprost is also currently approved in the EU for the decrease of elevated intraocular pressure in paediatric patients aged 2 months to < 18 years with ocular hypertension or paediatric glaucoma [L5146, L5152].', '["S01EE04"]', '[]');
