INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29096, 'Venlafaxine 150 mg sr 7 caps.', 'فينلافاكسين 150مجم 7 كبسول', '27', '', 'Psychiatric.antidepressants', 'Venlafaxine', 'Global napi pharmaceuticals', 'Cap', 'كبسولة', '150 mg', '1', '', 'about venlafaxine an arylalkanolamine derivative serotonin/norepinephrine reuptakeinhibitor antidepressant anxiolytic. mechanism of action of venlafaxine venlafaxine and its metabolite block the re-uptake of neurotransmitter serotonin and noradrenaline fr', '', '', 1254, '2022-07-12', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Venlafaxine is an antidepressant and a serotonin and norepinephrine reuptake inhibitor (SNRI). Its active metabolite, [desvenlafaxine], works by blocking the reuptake of serotonin and norepinephrine, which are key neurotransmitters in mood regulation. Venlafaxine is officially approved to treat major depressive disorder (MDD), generalized anxiety disorder (GAD), social anxiety disorder, and panic disorder in adults.[L43030] The immediate formulation of the drug, marketed as Effexor, was first approved by the FDA in 1993 and the extended-release formulation, Effexor XR, was later introduced in 1997.[A252065]

