INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29143, 'Verdapiotal 10mg 30 f.c.tablets', 'فيردابيوتال 10مجم 30 قرص', '140', '63', 'Psychiatric.antidepressants', 'Escitalopram', 'Global napi pharmaceuticals > global advanced pharmaceutical (gap)', 'Tablet', 'أقراص', '10mg', '3', 'مضاد اكتئاب', '', '6223005940179', '', 6376, '2023-12-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Escitalopram is a selective serotonin re-uptake inhibitor (SSRI) and the S-enantiomer of racemic [citalopram].[A185420] It is used to restore serotonergic function in the treatment of depression and anxiety.[L8513,L8516,L8522] Escitalopram is approximately 150 times more potent than citalopram’s R-enantiomer and is responsible for the vast majority of citalopram’s clinical activity, with some evidence suggesting that the R-enantiomer of racemic citalopram actively dampens the activity of escitalopram rather than existing simply as an inactive enantiomer.[A39738,A185819] Amongst SSRIs, escitalopram exerts the highest degree of selectivity for the serotonin transporter (SERT) relative to other off-targets which may explain its lower rates of adverse effects as compared to other agents in this class.[A185726] Escitalopram also differentiates itself from other SSRIs via allosteric action on its target - this may be the mechanism responsible for its observed superior efficacy and faster onset compared to other SSRIs.[A185825,A185726,A185822]', '["N06AB10"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29144, 'Verdenodeb 20mg 4 f.c. tabs.', 'فيردينوديب 20مجم 4 اقراص', '53', '28.8', 'Tonic for men', 'Vardenafil', 'Debeiky > novell pharma', 'Tab', 'أقراص', '20mg', '1', '', '', '', '', 3558, '2025-08-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Vardenafil (Levitra) is an oral therapy for the treatment of erectile dysfunction. It is a selective inhibitor of cyclic guanosine monophosphate (cGMP)-specific phosphodiesterase type 5 (PDE5). Penile erection is a hemodynamic process initiated by the relaxation of smooth muscle in the corpus cavernosum and its associated arterioles. During sexual stimulation, nitric oxide is released from nerve endings and endothelial cells in the corpus cavernosum. Nitric oxide activates the enzyme guanylate cyclase resulting in increased synthesis of cyclic guanosine monophosphate (cGMP) in the smooth muscle cells of the corpus cavernosum. The cGMP in turn triggers smooth muscle relaxation, allowing increased blood flow into the penis, resulting in erection. The tissue concentration of cGMP is regulated by both the rates of synthesis and degradation via phosphodiesterases (PDEs). The most abundant PDE in the human corpus cavernosum is the cGMPspecific phosphodiesterase type 5 (PDE5); therefore, the inhibition of PDE5 enhances erectile function by increasing the amount of cGMP.', '["G04BE09"]', '[]');
