INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29366, 'Vinorelbine actavis 10mg vial(n/a)', 'فينوريلبين اكتيفس 10مجم فيال', '130', '', 'Antineoplastic', 'Vinorelbine', 'Actavis > multipharma', 'Vial', 'فيال', '10mg', '1', '', '', '', '', 699, '2022-07-25', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Vinorelbine is an anti-mitotic chemotherapy drug that is used in the treatment of several types of malignancies, including breast cancer and non-small cell lung cancer (NSCLC) [L1998].  It was initially approved in the USA in 1990''s for the treatment of NSCLC [L2010].

