INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29890, 'Zesperone 2mg 10 f.c. tab.', 'زيسبيرون 2مجم 10 اقراص', '42', '30', 'Psychiatric.antipsychotics', 'Risperidone', 'Global napi pharmaceuticals > rexcel', 'Tab', 'أقراص', '2mg', '1', '', 'about risperidone second-generation antipsychotic benzisoxazole derivative antimanic atypical antipsychotic. mechanism of action of risperidone risperidone exerts its antipsychotic activity by blocking dopamine and serotonin (5ht2) receptors. the drug has', '', '', 1429, '2023-12-20', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Risperidone is a second-generation antipsychotic (SGA) medication used in the treatment of a number of mood and mental health conditions including schizophrenia and bipolar disorder.[L12885] It is one of the most widely used SGAs. [Paliperidone], another commonly used SGA, is the primary active metabolite of risperidone (i.e. 9-hydroxyrisperidone).[L12885]

