INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (30661, 'Hypercapt 5mg/5ml syrup 100 ml', 'هايبركابت 5مجم/5مل شراب 100 مل', '34', '26', 'Antihypertensive', 'Captopril', 'Royal link > new boston pharmaceuticals', 'Cap', 'كبسولة', '5mg', '1', 'علاج ضغط الدم', 'oral antihypertensive ace inhibitor for 1. hypertension 2.heart failure 3. others', '6224010899018', '', 2241, '2024-09-14', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Captopril is a potent, competitive inhibitor of angiotensin-converting enzyme (ACE), the enzyme responsible for the conversion of angiotensin I (ATI) to angiotensin II (ATII). ATII regulates blood pressure and is a key component of the renin-angiotensin-aldosterone system (RAAS). Captopril may be used in the treatment of hypertension.', '["C09AA01", "C09BA01"]', '[]');
