INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (30976, 'Nerhacomine 12.5mg 20 orodispersible films', 'نيرهاكومين 12.5مجم 20 فيلم', '35', '', 'H1 antagonist', 'Diphenhydramine', 'Nerhadou international co.', 'Film', 'فيلم', '12.5mg', '1', 'مضاد للهيستامين يستخدم فى علاج لحكة.الطفح الجلدي.سيلان الأنف.حكة الحلق.دمع العينين.العطس', 'about diphenhydramine first generation h1 antagonist ethanolamine derivative antihistamine antiemetic antivertigo sedative and hypnotic. mechanism of action of diphenhydramine antiallergic action: it produces its antihistamine action by competitively bloc', '', '', 1283, '', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Diphenhydramine - perhaps known most commonly as its brand name formulation Benadryl - is a first-generation H1 receptor antihistamine that is used extensively for the treatment of seasonal allergies, insect bites and stings, and rashes [L5263, L5266, L5269, F3379]. However, it also has antiemetic, antitussive, hypnotic, and antiparkinson properties [L5269, F3352]. As histamine receptors exist both peripherally and in the central nervous system, diphenhydramine has been shown to cause sedation due to its competitive antagonism of histamine H1 receptors within the central nervous system [L5263, L5266, L5269, F3379, A174541]. While its use in allergy therapy can sometimes fall out of favor due to its sedative effect, diphenhydramine has been repurposed for use within many non-prescription over-the-counter sleep aids and cough-and-cold medications that have been marketed for "night time" use [L5263, L5281, L5287]. 

Diphenhydramine is also used in combination with [DB14132] as the anti-nausea drug [DB00985] where it is utilized primarily for its antagonism of H1 histamine receptors within the vestibular system [A1540].

Diphenhydramine has also been shown to be implicated in a number of neurotransmitter systems that affect behaviour including dopamine, norepinephrine, serotonin, acetylcholine, and opioid [A1539]. As a result, diphenhydramine is being investigated for its anxiolytic and anti-depressant properties.', '["D04AA32", "R06AA02", "D04AA33", "R06AA52"]', '[]');
