INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (32703, 'Danasetron 4mg/2ml 5 amps.', 'داناسيترون 4 مجم/2مل 5 امبول', '120', '82.5', 'Antiemetic', 'Ondansetron', 'Sunny medical', 'Amp', 'أمبول', '4mg', '5', '', 'about ondansetron selective serotonin 5-ht3 receptor antagonist imidazole derivative antiemetic. mechanism of action of ondansetron ondansetron is a selective 5ht3 receptor antagonist. 5ht3 receptors are present in vagal nerve terminals and chemoreceptor', '6251065023716', '', 880, '2024-12-21', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'A competitive serotonin type 3 receptor antagonist. It is effective in the treatment of nausea and vomiting caused by cytotoxic chemotherapy drugs, including cisplatin, and has reported anxiolytic and neuroleptic properties.

Having been developed in the 1980s by GlaxoSmithKline and approved by the US FDA since January 1991, ondansetron has demonstrated a long history of use and efficacy. Commonly formulated as oral tablets, orally disintegrating tablets (ODT), and injections, and available as generic products as well, ondansetron continues to see contemporary innovations in its formulation and use, including the development of orally soluble films that are both discreet in administration and less of a burden in comparison to having patients attempt to swallow pills during emesis.[L5221]

