INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (33445, 'Protofenac 150 mg 20 sust. rel. tabs.', 'بروتوفيناك 150مجم 20 قرص', '92', '36', 'Nsaid.acetic acid derivatives', 'Diclofenac sodium', 'Penta pharma', 'Tab', 'أقراص', '150 mg', '2', 'مضاد للالتهاب ومسكن', '', '6224007738900', '', 3503, '2024-06-02', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Diclofenac is a phenylacetic acid derivative and non-steroidal anti-inflammatory drug (NSAID).[label] NSAIDs inhibit cyclooxygenase (COX)-1 and-2 which are the enzyme responsible for producing prostaglandins (PGs). PGs contribute to inflammation and pain signalling. Diclofenac, like other NSAIDs, is often used as first line therapy for acute and chronic pain and inflammation from a variety of causes. Diclofenac was the product of rational drug design based on the structures of [phenylbutazone], [mefenamic acid], and [indomethacin].[A180796] The addition of two chlorine groups in the ortho position of the phenyl ring locks the ring in maximal torsion which appears to be related to increased potency. It is often used in combination with [misoprostol] to prevent NSAID-induced gastric ulcers. Diclofenac was first approved by the FDA in July 1988 under the trade name Voltaren, marketed by Novartis (previously Ciba-Geigy).[L7360]', '["S01CC01", "S01BC03", "M01AB55", "M02AA15", "M01AB05", "D11AX18"]', '[]');
