INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1100, 16480, 'Lactic acid', 'Acidosis, Lactic', 'Lactate metabolism results in production of bicarbonate.  Bicarbonate generation is impaired in patients with compromised liver function and/or disorders that decrease tissue perfusion and increase anoxia.  Therapy with sodium lactate should not be used in conditions in which lactate levels are increased (e.g., shock, congestive heart failure, respiratory alkalosis) or in which utilization of lactate is diminished (e.g., anoxia, beriberi).  Sodium lactate is not for use in the treatment of lactic acidosis.', '3', '', 'DDInter', 0);
