INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1101, 19061, 'Lactic acid', 'Acidosis, Lactic', 'Lactate metabolism results in production of bicarbonate.  Bicarbonate generation is impaired in patients with compromised liver function and/or disorders that decrease tissue perfusion and increase anoxia.  Therapy with sodium lactate should not be used in conditions in which lactate levels are increased (e.g., shock, congestive heart failure, respiratory alkalosis) or in which utilization of lactate is diminished (e.g., anoxia, beriberi).  Sodium lactate is not for use in the treatment of lactic acidosis.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1102, 16480, 'Lactic acid', 'Acid-Base Imbalance', 'Alkalinizing agents act as proton acceptors and/or dissociate to provide bicarbonate ions.  Elimination of bicarbonate is decreased in patients with renal impairment and can result in metabolic alkalosis.  Symptoms of metabolic alkalosis include hyperirritability or tetany, arrhythmia, and/or seizures (altered pH = altered calcium), or lactic acidosis due to impaired oxygen release.  Therapy with alkalinizing agents should be administered with extreme caution in patients with compromised renal function.  Clinical monitoring of renal function, acid/base balance and electrolytes is recommended.', '2', '', 'DDInter', 0);
