INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1180, 0, 'Ixekizumab', 'Infections', 'Upper respiratory tract infections, oral candidiasis, conjunctivitis, and tinea infections have occurred with the use of ixekizumab.  Close monitoring or discontinuation of therapy is recommended in patients with serious infections that do not respond to conventional treatment.  It is recommended to restart therapy only until the infection resolves.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1181, 0, 'Ixekizumab', 'Inflammatory Bowel Diseases', 'Crohn''s disease and ulcerative colitis, including exacerbations have occurred during ixekizumab treatment.  Caution and close monitoring for onset or exacerbation of inflammatory bowel disease are recommended.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1182, 0, 'Ixekizumab', 'Tuberculosis', 'Patients should be evaluated for tuberculosis (TB) infection prior to ixekizumab treatment.  It is recommended to initiate treatment of latent TB prior to administering ixekizumab and to consider anti-TB therapy prior to initiating therapy in patients with a past history of latent or active TB in whom an adequate course of treatment cannot be confirmed.  Do not administer this agent to patients with active TB infection.  Patients should be monitored closely for signs and symptoms of active TB during and after treatment.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1183, 0, 'Ixekizumab', 'Liver Diseases', 'No formal clinical studies of the effect of hepatic or renal impairment on the pharmacokinetics of ixekizumab have been conducted.  Care should be taken when prescribing this drug to these patients.', '1', '', 'DDInter', 0);
