INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1201, 23610, 'Ixazomib', 'Hepatic Insufficiency', 'Pharmacokinetic studies of ixazomib in patients with moderate to severe hepatic impairment, showed an increase AUC when compared to patients with normal hepatic function.  The pharmacokinetic of ixazomib was similar in patients with normal hepatic function and in patients with mild hepatic impairment.  It is recommended to reduce the starting dose in patients with moderate to severe hepatic impairment.  Caution should be exercised when ixazomib is administered to patients with moderate to severe hepatic function and monitoring of liver function tests is recommended as appropriate.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1202, 23608, 'Ixazomib', 'Peripheral Nervous System Diseases', 'Peripheral neuropathy has been reported with the use of ixazomib.  Patients should be monitored for symptoms of neuropathy and if they are experiencing new or worsening peripheral neuropathy, it is recommended to withhold therapy and modify the dose as per manufacturer instructions.', '2', '', 'DDInter', 0);
