INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1509, 7076, 'Lamivudine', 'Liver Diseases', 'Hepatotoxicity including lactic acidosis, severe hepatomegaly with steatosis, fulminant hepatitis, and hepatic failure has been associated with the use of some nucleoside reverse transcriptase inhibitors (NRTIs) alone or in combination with other antiretroviral agents.  Therapy with NRTIs should be administered cautiously in patients with preexisting liver disease, a history of alcohol abuse, or hepatitis.  Therapy should be suspended if clinical or laboratory findings suggestive of lactic acidosis or pronounced hepatotoxicity occur.  The use of abacavir is contraindicated in patients with moderate to severe hepatic impairment as its safety and efficacy has not been established on these patients.', '3', '', 'DDInter', 0);
