INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1517, 7076, 'Lamivudine', 'Kidney Diseases', 'Patients with clinically significant renal impairment may be at greater risk for toxicities and adverse effects from nucleoside reverse transcriptase inhibitors (NRTIs) due to decreased drug clearance.  Dosage adjustments are recommended.  In addition, these patients should be monitored closely during NRTI therapy, and dosages adjusted further if necessary.', '3', '', 'DDInter', 0);
