INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1701, 28955, 'Leflunomide', 'Tuberculosis', 'Patients should be evaluated for active and latent tuberculosis (TB) infection prior to leflunomide treatment.  Leflunomide has not been studied in patients with a positive tuberculosis screen, and the safety of leflunomide in individuals with latent tuberculosis infection is unknown.  Patients testing positive in tuberculosis screening should be treated by standard medical practice prior to therapy with leflunomide and monitored carefully during leflunomide treatment for possible reactivation of the infection.', '3', '', 'DDInter', 0);
