INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1776, 314, 'Letrozole', 'Hypercholesterolemia', 'In an adjuvant clinical trial hypercholesterolemia was reported in 52.3% of letrozole patients and 28.6% of tamoxifen patients.  CTC grade 3-4 hypercholesterolemia was reported in 0.4% of letrozole patients and 0.1% of tamoxifen patients.  Also in the adjuvant setting, an increase of >=1.5 X ULN in total cholesterol (generally non-fasting) was observed in patients on monotherapy who had baseline total serum cholesterol within the normal range (i.e., <=1.5 X ULN) in 151/1843 (8.2%) on letrozole vs 57/1840 (3.2%).  Caution is recommended when prescribing letrozole to these patients.  Consideration should be given to monitoring serum cholesterol.', '2', '', 'DDInter', 0);
