INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1794, 21700, 'Lenvatinib', 'Thromboembolism', 'In clinical trials, arterial thromboembolic events were reported in patients treated with lenvatinib.  It is recommended to discontinue lenvatinib following an arterial thrombotic event.  Care should be exercised when resuming lenvatinib after an arterial thromboembolic event as its safety has not been established and its use has not been studied in patients who have had an arterial thromboembolic event within the previous 6 months.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1795, 21701, 'Lenvatinib', 'Thromboembolism', 'In clinical trials, arterial thromboembolic events were reported in patients treated with lenvatinib.  It is recommended to discontinue lenvatinib following an arterial thrombotic event.  Care should be exercised when resuming lenvatinib after an arterial thromboembolic event as its safety has not been established and its use has not been studied in patients who have had an arterial thromboembolic event within the previous 6 months.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1796, 21700, 'Lenvatinib', 'Hemorrhage', 'Hemorrhagic events, including fatal hemorrhagic events have been reported with the use of lenvatinib.  It is recommended to withhold treatment and to either resume at a reduced dose or discontinue treatment depending on the severity and persistence of hemorrhage.  Discontinue therapy in patients who experience severe or fatal hemorrhagic events.  Prior to initiating treatment, consider the risk of severe or fatal hemorrhage associated with tumor invasion/infiltration of major blood vessels (e.g.  carotid artery).', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1797, 21701, 'Lenvatinib', 'Hemorrhage', 'Hemorrhagic events, including fatal hemorrhagic events have been reported with the use of lenvatinib.  It is recommended to withhold treatment and to either resume at a reduced dose or discontinue treatment depending on the severity and persistence of hemorrhage.  Discontinue therapy in patients who experience severe or fatal hemorrhagic events.  Prior to initiating treatment, consider the risk of severe or fatal hemorrhage associated with tumor invasion/infiltration of major blood vessels (e.g.  carotid artery).', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1798, 21700, 'Lenvatinib', 'Heart Failure', 'Cardiac dysfunctions, defined as decreased left or right ventricular function, cardiac failure, or pulmonary edema have been reported with the use of lenvatinib.  It is recommended to monitor patients for clinical symptoms or signs of cardiac decompensation.  Withhold lenvatinib therapy for development of Grade 3 cardiac dysfunction until improved and either resume at a reduced dose or discontinue its use depending on the severity and persistence of cardiac dysfunction.  Discontinue lenvatinib for Grade 4 cardiac dysfunction.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1799, 21701, 'Lenvatinib', 'Heart Failure', 'Cardiac dysfunctions, defined as decreased left or right ventricular function, cardiac failure, or pulmonary edema have been reported with the use of lenvatinib.  It is recommended to monitor patients for clinical symptoms or signs of cardiac decompensation.  Withhold lenvatinib therapy for development of Grade 3 cardiac dysfunction until improved and either resume at a reduced dose or discontinue its use depending on the severity and persistence of cardiac dysfunction.  Discontinue lenvatinib for Grade 4 cardiac dysfunction.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1800, 21700, 'Lenvatinib', 'Diarrhea', 'Cases of diarrhea have been reported with the use of lenvatinib, sometimes leading to interruption of therapy.  It is recommended to initiate prompt medical management for the development of diarrhea and to monitor for dehydration.  It might be necessary to interrupt treatment with lenvatinib or resume at a reduced dose or permanently discontinue treatment with lenvatinib if severe or fatal diarrhea persists despite medical management.  Care and close monitoring is recommended when using this agent in patients presenting diarrhea or at risk of developing diarrhea.', '2', '', 'DDInter', 0);
