INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1804, 21700, 'Lenvatinib', 'Hepatic Insufficiency', 'Hepatic failure, including fatal events have been reported with the use of lenvatinib.  No dose adjustment is recommended in patients with mild or moderate hepatic impairment.  Dose adjustment is needed in patients with severe hepatic impairment.  It is recommended to monitor liver function before initiation of therapy with lenvatinib and regularly thereafter as clinically indicated.  It might be necessary to temporarily withhold the use of lenvatinib and either resume at a reduced dose or discontinue treatment with lenvatinib depending on the severity and persistence of hepatic impairment.  Discontinue treatment with lenvatinib for hepatic failure.', '2', '', 'DDInter', 0);
