INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1806, 21700, 'Lenvatinib', 'Hypertension', 'The use of lenvatinib may cause hypertension.  It is recommended to control blood pressure prior to treatment with lenvatinib and monitor blood pressure regularly during treatment as medically indicated.  Withhold treatment with lenvatinib for Grade 3 hypertension despite optimal antihypertensive therapy.  If clinically appropriate, resume at a reduced dose when hypertension is controlled and discontinue treatment for hypertension that becomes life-threatening.  Care and close monitoring of blood pressure is recommended.', '2', '', 'DDInter', 0);
