INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1808, 21700, 'Lenvatinib', 'Hypocalcemia', 'Cases of hypocalcemia have been reported with the use of lenvatinib.  It is recommended to monitor blood calcium levels at least monthly and replace calcium as necessary during treatment and to interrupt and adjust dosing as necessary depending on severity, presence of ECG changes, and persistence of hypocalcemia.  Care should be exercised when using this agent in patients at risk.', '2', '', 'DDInter', 0);
