INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1810, 21700, 'Lenvatinib', 'Hypothyroidism', 'The use of lenvatinib impairs exogenous thyroid suppression and may cause hypothyroidism.  It is recommended to monitor thyroid function before initiation of, and at least monthly throughout, treatment with lenvatinib.  Care should be taken when using this agent in patients with thyroid dysfunction and treatment of hypothyroidism should be made according to standard medical practice to maintain a euthyroid state.', '2', '', 'DDInter', 0);
