INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1814, 21700, 'Lenvatinib', 'Long QT Syndrome', 'The use of lenvatinib may cause QT/QTc interval prolongation.  It is recommended to monitor and correct electrolyte abnormalities in all patients and to monitor electrocardiograms in patients with congenital long QT syndrome, congestive heart failure, bradyarrhythmias, or those who are taking drugs known to prolong the QT interval, including Class Ia and III antiarrhythmics.  Withhold treatment with lenvatinib for the development of QTc interval prolongation greater than 500 ms.  and resume treatment at a reduced dose when QTc prolongation resolves to baseline.', '2', '', 'DDInter', 0);
