INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1848, 22342, 'Levobunolol (ophthalmic)', 'Myasthenia Gravis', 'Topically applied beta-adrenergic receptor blocking agents (aka beta-blockers) are systemically absorbed, with the potential for producing clinically significant systemic effects even at low or undetectable plasma levels.  In the nervous and musculoskeletal systems, beta blockade may potentiate muscle weakness consistent with certain myasthenic symptoms such as diplopia, ptosis, and generalized weakness.  Several beta-blockers have been associated rarely with aggravation of muscle weakness in patients with preexisting myasthenia gravis or myasthenic symptoms.', '2', '', 'DDInter', 0);
