INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1901, 24942, 'Levamlodipine', 'Coronary Artery Disease', 'Increased frequency, duration, and/or severity of angina, as well as acute myocardial infarction, have rarely developed during initiation or dosage increase of calcium channel blockers (CCBs), particularly in patients with severe obstructive coronary artery disease and those treated with immediate-release formulations.  The mechanism of this effect is not established.  Therapy with CCBs should be administered cautiously in patients with significant coronary artery disease.', '3', '', 'DDInter', 0);
