INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1936, 7612, 'Leuprolide', 'Cardiovascular Diseases', 'Increased risk of developing myocardial infarction, sudden cardiac death and stroke has been reported in association with the use of GnRH agonists such as leuprolide, and triptorelin.  Cardiovascular risk factors should be evaluated carefully before treatment initiation.  Patients should be monitored for symptoms and signs suggestive of development or cardiovascular disease and should be managed according to current clinical practice.', '2', '', 'DDInter', 0);
