INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (2018, 0, 'Levoleucovorin', 'Seizures', 'Levoleucovorin is the levo isomeric form of racemic d,l-leucovorin, present as the calcium salt.  Seizures and/or syncope have been reported rarely in cancer patients receiving d,l-leucovorin, usually in association with fluoropyrimidine administration, and most commonly in those with CNS metastases or other predisposing factors.  Care is recommended when using this agent in patient with seizure disorders or at risk of seizures even though a causal relationship has not been established.', '2', '', 'DDInter', 0);
