INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (2775, 326, 'Levofloxacin', 'Kidney Diseases', 'Quinolones (except trovafloxacin, moxifloxacin, and nalidixic acid) and their metabolites are eliminated by the kidney.  Patients with renal impairment may be at greater risk for adverse effects from quinolones, including nephrotoxicity, due to decreased drug clearance.  Dosage adjustments may be necessary and modifications should be based on the degree of renal impairment and severity of infection in accordance with the individual product package labeling.  Renal function tests should be performed periodically during therapy.', '2', '', 'DDInter', 0);
