INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3301, 29789, 'Lidocaine', 'Cardiomyopathies', 'Antiarrhythmic agents can induce severe hypotension (particularly with IV administration) or induce or worsen congestive heart failure (CHF).  Patients with primary cardiomyopathy or inadequately compensated CHF are at increased risk.  Antiarrhythmic agents should be administered cautiously and dosage and/or frequency of administration modified in patients with hypotension or adequately compensated CHF.  Alternative therapy should be considered unless these conditions are secondary to cardiac arrhythmia.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3302, 29790, 'Lidocaine', 'Cardiomyopathies', 'Antiarrhythmic agents can induce severe hypotension (particularly with IV administration) or induce or worsen congestive heart failure (CHF).  Patients with primary cardiomyopathy or inadequately compensated CHF are at increased risk.  Antiarrhythmic agents should be administered cautiously and dosage and/or frequency of administration modified in patients with hypotension or adequately compensated CHF.  Alternative therapy should be considered unless these conditions are secondary to cardiac arrhythmia.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3303, 3338, 'Lidocaine', 'Arrhythmias, Cardiac', 'Antiarrhythmic agents can induce or worsen ventricular arrhythmias.  Ventricular tachycardia, ventricular fibrillation, and torsades de pointes have occurred in some patients.  Patients with underlying cardiac dysfunction, bradycardia, hypokalemia, hypomagnesemia, or high antiarrhythmic serum concentrations are at increased risk for drug-induced arrhythmias.  Therapy with antiarrhythmics should be used with extreme caution in patients with or predisposed to arrhythmias.  Evidence of improved survival is lacking for use of antiarrhythmic therapy in asymptomatic, non-life-threatening arrhythmias.  Therapy with antiarrhythmic agents should be reserved for patients with life-threatening arrhythmias.', '3', '', 'DDInter', 0);
