INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3565, 12971, 'Liraglutide', 'Thyroid Neoplasms', 'GLP-1 receptor agonist antidiabetic drugs are contraindicated in patients with a personal or family history of medullary thyroid carcinoma and in patients with Multiple Endocrine Neoplasia syndrome type 2 (MEN2).  Carcinogenicity studies in rodents and limited postmarketing data suggest that GLP-1 inhibitors may cause a dose-related and treatment duration-related increase in risk of thyroid C-cell tumors, although a causal relationship has not been established.', '3', '', 'DDInter', 0);
