INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3567, 12971, 'Liraglutide', 'Depressive Disorder', 'Therapy with liraglutide should be administered cautiously in patients with depression or any mood or behavior disorder because of the potential for suicidal thoughts or behavior.  The drug should be avoided in patients with a history of suicidal attempts or active suicidal ideation.', '3', '', 'DDInter', 0);
