INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3569, 12971, 'Liraglutide', 'Kidney Diseases', 'There have been reports of acute renal failure and worsening of chronic renal failure sometimes requiring hemodialysis in patients treated with GLP-1 receptor inhibitors.  Some of these events occurred in patients without known underlying renal disease.  These drugs should be used with caution in patients with renal impairment, especially during treatment initiation or dose escalation.  Patients should be monitored, especially if they report severe gastrointestinal reactions such as nausea, vomiting, diarrhea, or dehydration.  The frequency of gastrointestinal events is increased as renal function declines.  There is limited clinical experience with most of these drugs in patients with severe renal impairment.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3570, 12972, 'Liraglutide', 'Kidney Diseases', 'There have been reports of acute renal failure and worsening of chronic renal failure sometimes requiring hemodialysis in patients treated with GLP-1 receptor inhibitors.  Some of these events occurred in patients without known underlying renal disease.  These drugs should be used with caution in patients with renal impairment, especially during treatment initiation or dose escalation.  Patients should be monitored, especially if they report severe gastrointestinal reactions such as nausea, vomiting, diarrhea, or dehydration.  The frequency of gastrointestinal events is increased as renal function declines.  There is limited clinical experience with most of these drugs in patients with severe renal impairment.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3571, 12971, 'Liraglutide', 'Cardiovascular Diseases', 'Liraglutide can increase the resting heart rate and it should be used with caution in patients with history of cardiovascular disease and cerebrovascular disease.  Heart rate should be monitored regularly, and patients should inform their healthcare providers of any symptoms such as palpitations and racing heartbeat appear while at rest.', '2', '', 'DDInter', 0);
