INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3601, 18173, 'Apixaban', 'Hemorrhage', 'The use of factor Xa inhibitors is contraindicated in patients with active pathological bleeding as these agents increase the risk of bleeding and can cause serious or fatal hemorrhages.  Caution is recommended when prescribing factor Xa inhibitors to patients at increased risk of bleeding, including patients undergoing procedures where bleeding may cause serious complications.  Such patients include those undergoing spinal/epidural anesthesia or spinal puncture; these patients are at high risk of an epidural or spinal hematoma, which can result in long-term or permanent paralysis.  In general, when using factor Xa inhibitors, the risk of thrombotic events should be weighed against the risk of bleeding.', '3', '', 'DDInter', 0);
