INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3701, 31410, 'Apixaban', 'Antiphospholipid Syndrome', 'Direct acting oral anticoagulants including factor Xa inhibitors and some thrombin inhibitors as dabigatran, are not recommended for use in patients with antiphospholipid syndrome (APS).  Treatment with these drugs has been associated with increased rates of recurrent thrombotic events, especially in patients with triple positive APS.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3702, 27247, 'Lixisenatide', 'Angioedema', 'The use of lixisenatide may result in cross-sensitivity in patients with a history of anaphylaxis reaction or angioedema to another GLP-1 receptor agonist.  These patients should be informed of the risk of anaphylaxis when using lixisenatide.  Close monitoring is recommended when prescribing lixisenatide to these patients.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3703, 27248, 'Lixisenatide', 'Angioedema', 'The use of lixisenatide may result in cross-sensitivity in patients with a history of anaphylaxis reaction or angioedema to another GLP-1 receptor agonist.  These patients should be informed of the risk of anaphylaxis when using lixisenatide.  Close monitoring is recommended when prescribing lixisenatide to these patients.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3704, 27247, 'Lixisenatide', 'Gastroparesis', 'Lixisenatide slows gastric emptying and patients with preexisting gastroparesis were excluded from clinical trials.  Lixisenatide should not be used in patients with severe gastroparesis.', '3', '', 'DDInter', 0);
