INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4001, 12965, 'Linezolid', 'Malignant Carcinoid Syndrome', 'Linezolid is a weak, reversible, nonselective monoamine oxidase inhibitor (MAOI).  Nonspecific MAOIs inhibit the breakdown of pressor amines, including serotonin, and may exacerbate symptoms of the carcinoid syndrome.  While adverse effects related to MAOI activity have not been reported with linezolid, the potential for excessive serotonergic effects should be considered.  Therapy with linezolid should be administered cautiously in patients with carcinoid syndrome and in patients receiving serotonergic agents.  Clinical data are not available concerning the use of linezolid in these populations.', '2', '', 'DDInter', 0);
