INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4171, 1162, 'Linezolid', 'Peripheral Nervous System Diseases', 'Peripheral and optic neuropathies have been reported in patients treated with linezolid.  If patients experience symptoms of visual impairment, such as changes in visual acuity, changes in color vision, blurred vision, or visual field defect, prompt ophthalmic evaluation is recommended.  Visual function should be monitored in all patients taking linezolid for extended periods and in all patients reporting new visual symptoms regardless of length of therapy with linezolid.  If peripheral or optic neuropathy occurs, the continued use of linezolid in these patients should be weighed against the potential risks.', '2', '', 'DDInter', 0);
