INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4258, 8332, 'Apomorphine', 'Hypotension', 'Dopamine agonists may impair the systemic regulation of blood pressure, with resultant orthostatic hypotension, especially during dose escalation.  Therapy with dopamine agonists should be monitored carefully in patients with Parkinson''s disease since they may have an impaired ability to respond to an orthostatic challenge, and also in patients receiving antihypertensive drugs.', '3', '', 'DDInter', 0);
