INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4271, 5593, 'Acarbose', 'Kidney Diseases', 'Acarbose may accumulate in patients with renal impairment.  Following oral administration, 35% of a dose is recovered in the urine as either the parent drug or metabolites.  However, long-term clinical trials in diabetic patients with significant renal dysfunction (serum creatinine > 2.0 mg/dL) have not been conducted.  Therapy with acarbose should be administered cautiously in patients with renal dysfunction.  The manufacturer does not recommend its use in patients with significantly impaired renal function.', '3', '', 'DDInter', 0);
