INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4275, 5593, 'Acarbose', 'Intestinal Diseases', 'The use of alpha-glucosidase inhibitors is contraindicated in patients with inflammatory bowel disease, colonic ulceration, partial intestinal obstruction, or any chronic intestinal disease associated with marked disorders of digestion or absorption.  Alpha-glucosidase inhibitors competitively inhibit enzymes involved in the digestion of carbohydrates.  Increased gas formation in the intestines due to fermentation of the undigested carbohydrates can worsen or aggravate intestinal problems.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4276, 24143, 'Acarbose', 'Intestinal Diseases', 'The use of alpha-glucosidase inhibitors is contraindicated in patients with inflammatory bowel disease, colonic ulceration, partial intestinal obstruction, or any chronic intestinal disease associated with marked disorders of digestion or absorption.  Alpha-glucosidase inhibitors competitively inhibit enzymes involved in the digestion of carbohydrates.  Increased gas formation in the intestines due to fermentation of the undigested carbohydrates can worsen or aggravate intestinal problems.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4277, 5593, 'Acarbose', 'Liver Diseases', 'The use of acarbose has been associated with dose-related elevations in serum transaminase levels and, rarely, hyperbilirubinemia and jaundice.  Hepatic adverse effects may be more likely to occur at dosages exceeding 50 mg three times a day.  Therapy with acarbose should be administered cautiously in patients with liver disease.  Monitoring of serum transaminases is recommended every 3 months during the first year of treatment and periodically thereafter.  If hepatotoxicity is suspected at any time, a dosage reduction or withdrawal of therapy may be indicated.', '2', '', 'DDInter', 0);
