INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4433, 616, 'Losartan', 'Liver Diseases', 'Losartan is converted in the liver to an active carboxylic acid metabolite and several inactive metabolites, and both parent drug and metabolites are eliminated by the kidney (35%) as well as by biliary excretion (60%).  Dosage adjustments are not necessary in patients with renal impairment unless they are also volume-depleted, in which case therapy should be initiated under medical supervision.  In patients with cirrhosis, however, significantly increased plasma concentrations of parent drug and active metabolite have been reported.  Therapy with losartan should be initiated cautiously at a reduced dosage (50%) in patients with impaired liver function.', '2', '', 'DDInter', 0);
