INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4469, 7568, 'Lovastatin', 'Kidney Diseases', 'In patients with severe renal impairment (CrCl < 30 mL/min.), the plasma concentrations of total HMG-CoA reductase inhibitors after a single dose of lovastatin may be approximately two-fold higher than those in healthy patients, presumably due to the accumulation of active metabolites.  Increased HMG-CoA reductase inhibitory activity may be associated with a greater risk of adverse effects, including hepatic and musculoskeletal toxicities.  Therefore, lovastatin dosage increases above 20 mg/day should be considered and implemented cautiously in patients with severe renal impairment.  Close clinical monitoring is recommended during therapy.', '3', '', 'DDInter', 0);
