INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4490, 11849, 'Lorlatinib', 'Atrioventricular Block', 'PR interval prolongation and atrioventricular (AV) block can occur in patients receiving lorlatinib, with some reports of Grade 3 AV block that required a pacemaker placement.  Patients at risk should have a ECG prior to initiating lorlatinib and should be monitored periodically thereafter.  Withhold treatment and resume at a reduced dose or at the same dose in patients who undergo pacemaker placement.  Permanently discontinue for recurrence in patients without a pacemaker.', '2', '', 'DDInter', 0);
