INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4492, 11849, 'Lorlatinib', 'Central Nervous System Diseases', 'A broad spectrum of central nervous system (CNS) effects can occur in patients receiving lorlatinib.  These include seizures, hallucinations, changes in cognitive function, mood (including suicidal ideation), speech, mental status, and sleep.  Overall, CNS effects occurred in as many as 54% of patients receiving lorlatinib with some requiring treatment discontinuation.  Caution and close monitoring of patients with history of seizures or any mental or psychiatric illness is recommended.', '2', '', 'DDInter', 0);
