INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4494, 11849, 'Lorlatinib', 'Hepatic Insufficiency', 'The recommended dose of lorlatinib has not been established for patients with moderate or severe hepatic impairment, therefore, caution is advised if using in these patients.  Additionally, severe hepatotoxicity has been reported in patients with risk factors for hepatotoxicity (e.g., taking hepatotoxic drugs such as strong CYP3A inducers).  Caution and close monitoring of hepatic function is advised.', '2', '', 'DDInter', 0);
